window.addEvent('domready',function(){window.entityHelper=new EntityHelper()});Delegator.register('click',{'ReadForm':function(event,el,api){event.stop();entityHelper.resetForm();document.id('entity-add-wrapper').show()},'CancelAdd':function(event,el,api){event.stop();entityHelper.resetForm();document.id('entity-add-wrapper').hide()},'Add':function(event,el,api){event.stop();entityHelper.add()}})